import React, { useState } from 'react';
import axios from 'axios';
import '../styles.css';

function Submit() {
  const [restaurantName, setRestaurantName] = useState('');
  const [teamName, setTeamName] = useState('');
  const [message, setMessage] = useState('');

  const handleRestaurantNameChange = (event) => {
    setRestaurantName(event.target.value);
  };

  const handleTeamNameChange = (event) => {
    setTeamName(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!teamName || !restaurantName) {
      setMessage('Please enter values for both fields');
      return;
    }

    const response = await fetch(`http://localhost:5050/api/restaurants/${teamName}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: restaurantName }),
    });
    if (response.ok) {
      setMessage(`Restaurant ${restaurantName} added to Team ${teamName}`);
      setRestaurantName('');
    } else {
      setMessage('Error adding restaurant');
    }
  };

  return (
    <div className="container">

      <h2 className="text-center">Suggest a restaurant to your team</h2>
      <form onSubmit={handleSubmit} className="form">
        <label htmlFor="teamName">Team Name:</label>
        <input type="text" id="teamName" value={teamName} onChange={handleTeamNameChange} />

        <label htmlFor="restaurantName">Restaurant Name:</label>
        <input type="text" id="restaurantName" value={restaurantName} onChange={handleRestaurantNameChange} />

        <button type="submit">Add Restaurant</button>
        <p className="message">{message}</p>
      </form>
    </div>
  );
}

export default Submit;
