import React, { useState } from 'react';
import '../css/styles.css';
import $ from 'jquery';
import 'datatables.net';
import 'datatables.net-bs4/css/dataTables.bootstrap4.min.css';
import 'datatables.net-bs4/js/dataTables.bootstrap4.min.js';


function Reset() {
    const [teamName, setTeamName] = useState('');
    const [message, setMessage] = useState('');

    const handleTeamNameChange = (event) => {
        setTeamName(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const response = await fetch(`http://localhost:5050/api/restaurants/${teamName}/reset`, {
            method: 'DELETE',
        });
        if (response.ok) {
            setMessage(`All restaurants for team ${teamName} have been removed.`);
        } else {
            setMessage(`Team does not exist or no restaurants added`);
        }
    };

    const handleViewAll = async (event) => {
        event.preventDefault();
        const response = await fetch(`http://localhost:5050/api/restaurants/${teamName}`);
        if (response.ok) {
            const data = await response.json();
            console.log(data);
            setMessage(`All restaurants for team ${teamName}: ${data}`);
        } else {
            setMessage(`Team does not exist or no restaurants added`);
        }
    }

    return (
        <div className="container">
            <h1 className="text-center">Reset!</h1>
            <form onSubmit={handleViewAll} className="form">
                <label htmlFor="teamName">Team Name:</label>
                <input type='text' id='teamName' value={teamName} onChange={handleTeamNameChange} />
                <button type="submit" style={{ marginTop: '10px' }} className="random-button">View list of restaurants for team</button>
                <table id="restaurantTable" className="table table-striped table-bordered" style={{ width: '100%' }}>
                    <thead>
                        <tr>
                            <th>Name</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((restaurant) => (
                            <tr key={restaurant._id}>
                                <td>{restaurant.name}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                <button onClick="handleSubmit" style={{ marginTop: '10px' }} className="random-button">Reset Suggestions</button>
                <br></br>
                <p className="message">{message}</p>
            </form>
        </div>
    )
}


export default Reset;
