import React, { useState } from 'react';
import axios from 'axios';
import './sr'

function App() {
  const [restaurantName, setRestaurantName] = useState('');
  const [teamName, setTeamName] = useState('');
  const [message, setMessage] = useState('');

  const handleRestaurantNameChange = (event) => {
    setRestaurantName(event.target.value);
  };

  const handleTeamNameChange = (event) => {
    setTeamName(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const response = await fetch(`http://localhost:5050/api/restaurants/${teamName}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name: restaurantName }),
    });
    if (response.ok) {
      setMessage(`Restaurant ${restaurantName} added to team ${teamName}`);
      setRestaurantName('');
    } else {
      setMessage('Error adding restaurant');
    }
  };

  const handleGetRandomRestaurant = async () => {
    const response = await fetch(`http://localhost:5050/api/restaurants/${teamName}/random`);
    if (response.ok) {
      const data = await response.json();
      setMessage(`Random restaurant for team ${teamName}: ${data.name}`);
    } else {
      setMessage(`Error getting random restaurant for team ${teamName}`);
    }
  };

  return (
    <div className="container my-5">
      <h2 className="text-center">Restaurant Generator</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Restaurant Name:
          <input type="text" value={restaurantName} onChange={handleRestaurantNameChange} />
        </label>
        <br />
        <label>
          Team Name:
          <input type="text" value={teamName} onChange={handleTeamNameChange} />
        </label>
        <br />
        <button type="submit">Add Restaurant</button>
      </form>
      <br />
      <button onClick={handleGetRandomRestaurant}>Get Random Restaurant</button>
      <p>{message}</p>
    </div>
  );
}

export default App;
