import React, { useState } from 'react';
import axios from 'axios';
import '../css/styles.css';

function Reset() {
    const [teamName, setTeamName] = useState('');
    const [message, setMessage] = useState('');

    const handleTeamNameChange = (event) => {
        setTeamName(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const response = await Restaurant.deleteMany({ team: teamName })
        if (response.ok) {
            console.log('All restaurants for team ${teamName} have been removed.');
        } else {
            setMessage(`Team does not exist or no restaurants added`);
        }
    };


    return (
        <div className="container">
            <h2 className="text-center">Reset Team!</h2>
            <form onSubmit={handleSubmit} className="form">
                <label htmlFor="teamName">Team Name:</label>
                <input type='text' id='teamName' value={teamName} onChange={handleTeamNameChange} />

                <button type="submit" style={{ marginTop: '10px' }} className="random-button">Reset Team Suggestions</button>
                <p className="message">{message}</p>
            </form>
        </div>
    )
}

export default Reset;
